package news_test

import (
	"context"
	"log"
	"testing"
	"time"

	"github.com/danesparza/daydash-service/internal/news"
	"github.com/mitchellh/go-homedir"
	"github.com/spf13/viper"
	"go.mongodb.org/mongo-driver/bson/primitive"
)

func init() {
	// Find home directory.
	home, err := homedir.Dir()
	if err != nil {
		log.Fatalf("Couldn't find home directory - %v", err)
	}

	viper.AddConfigPath(home)              // adding home directory as first search path
	viper.AddConfigPath(".")               // also look in the working directory
	viper.SetConfigName("daydash-service") // name the config file (without extension)

	viper.AutomaticEnv() // read in environment variables that match

	// If a config file is found, read it in
	viper.ReadInConfig()
}

func TestNewsCache_GetAllNewsItems_DoesNotReturnError(t *testing.T) {
	ctx := context.Background()

	newsItems, err := news.GetAllStoredNewsItems(ctx)
	if err != nil {
		t.Errorf("Returned error and we didn't expect that: %v", err)
	}

	//	Spit out what we know:
	for _, item := range newsItems {
		t.Logf("News story: %v\n", item.Updates[0].Text)
	}
}

func TestNewsCache_GetMostRecentTweetID_GetsMostRecentTweet(t *testing.T) {
	ctx := context.TODO()

	tweetID := news.GetMostRecentTweetID(ctx)
	if tweetID == "" {
		t.Errorf("Returned an empty tweetid and we didn't expect that")
	}

	t.Logf("Most recent tweetID: %v\n", tweetID)
}

func TestNewsCache_GetStoryForUpdateID_ReturnsStory(t *testing.T) {
	ctx := context.TODO()
	updateID := "1537058621829394432"

	story := news.GetStoryForUpdateID(ctx, updateID)
	if story.URL == "" {
		t.Errorf("Returned an empty story and we didn't expect that")
	}

	t.Logf("Returned story: %v\n", story.Updates[0].Text)
}

func TestNewsCache_GetStoryForUrl_ReturnsStory(t *testing.T) {
	ctx := context.TODO()
	storyUrl := "https://www.cnn.com/2022/06/15/politics/joe-biden-oil-companies/index.html"

	story := news.GetStoryForUrl(ctx, storyUrl)
	if story.URL == "" {
		t.Errorf("Returned an empty story and we didn't expect that")
	}

	t.Logf("Returned story: %v\n", story.Updates[0].Text)
}

func TestNewsCache_GetStoryForUrl_ReturnsEmpty(t *testing.T) {
	ctx := context.TODO()
	storyUrl := "https://www.cnn.com/2022/06/15/politics/pence-oil-companies/index.html" // URL won't be found

	story := news.GetStoryForUrl(ctx, storyUrl)
	if story.URL != "" {
		t.Errorf("Returned a story and we didn't expect that")
	}

}

func TestNewsCache_AddNewsStory_StoryAdded(t *testing.T) {
	ctx := context.TODO()

	story := news.NewsStory{
		URL:      "https://www.cnn.com/2022/06/21/us/miami-plane-fire/index.html",
		ShortURL: "https://t.co/WOb901SFGZ",
		Entities: []news.NewsStoryEntity{},
		Updates: []news.NewsStoryUpdate{{
			ID:        "1537320729573826777",
			Text:      "Testing this update",
			Time:      int(time.Now().UTC().Unix()),
			Mediaurl:  "https://dynaimage.cdn.cnn.com/cnn/digital-images/org/331c15a6-3e1c-4463-9a45-266a58303223.jpg",
			Mediadata: "data:image/jpeg;base64,somelongstringhere",
		}},
	}

	err := news.AddNewsStory(ctx, story)
	if err != nil {
		t.Errorf("Returned an error and we didn't expect that: %v", err)
	}

}

func TestNewsCache_UpdateNewsStory_StoryUpdated(t *testing.T) {
	ctx := context.TODO()

	//	Get our ID to update
	id, err := primitive.ObjectIDFromHex("62a9de9782d8df76658e066d")
	if err != nil {
		t.Errorf("Returned an error converting to primitive and we didn't expect that: %v", err)
	}

	story := news.NewsStory{
		ID:       id,
		URL:      "https://www.cnn.com/2022/06/15/politics/joe-biden-oil-companies/index.html",
		ShortURL: "https://cnn.it/3zEUpI0",
		Entities: []news.NewsStoryEntity{{
			Type: "Person",
			Text: "Joe Biden",
		}},
		Updates: []news.NewsStoryUpdate{{
			ID:        "1537058621829394432",
			Text:      "President Biden is calling on major oil refinery companies to take \\\"immediate actions\\\" to ramp up supply as Americans continue to see soaring prices at the gas pump",
			Time:      1655298307,
			Mediaurl:  "https://cdn.cnn.com/cnnnext/dam/assets/220614225802-02-biden-inflation-super-tease.jpg",
			Mediadata: "data:image/jpeg;base64,",
		}, {
			ID:        "1537320729573826777",
			Text:      "Testing this update",
			Time:      int(time.Now().UTC().Unix()),
			Mediaurl:  "https://dynaimage.cdn.cnn.com/cnn/digital-images/org/331c15a6-3e1c-4463-9a45-266a58303223.jpg",
			Mediadata: "data:image/jpeg;base64,somelongstringhere",
		}},
	}

	err = news.UpdateNewsStory(ctx, story)
	if err != nil {
		t.Errorf("Returned an error and we didn't expect that: %v", err)
	}

}
